  -webkit-box-orient: vertical;
  display: -webkit-box;
  overflow: hidden;
  text-overflow: ellipsis;
  word-break: break-all;
}

.heading-divider {
  background: var(--primaryGradient);
  height: 4px;
  width: 50px;
  transform: skewX(-43deg);
  -webkit-transform: skewX(-43deg);
  -moz-transform: skewX(-43deg);
  -ms-transform: skewX(-43deg);
  -o-transform: skewX(-43deg);
}

.custom-divider {
  border-top: 1px solid var(--primary);
  width: 90px;
  position: relative;
}

.custom-divider::before {
  content: "";
  top: -2px;
  width: 28px;
  border-bottom: solid var(--white) 2px;
}

.custom-divider::after {
  content: "❯❯";
  top: -11px;
  color: var(--primary);
  font-size: 17px;
  letter-spacing: -1px;
  height: 20px;
  width: 26px;
}

.custom-divider::before,
.custom-divider::after {
  position: absolute;
  left: 0;
  line-height: 20px;
  font-weight: 600;
  text-align: center;
  animation: divider-move 7s infinite linear;
  -webkit-animation: divider-move 7s infinite linear;
}

@keyframes divider-move {
  0% {
    transform: translateX(-1px);
    -webkit-transform: translateX(-1px);
    -moz-transform: translateX(-1px);
    -ms-transform: translateX(-1px);
    -o-transform: translateX(-1px);
  }

  50% {
    transform: translateX(72px);
    -webkit-transform: translateX(72px);
    -moz-transform: translateX(72px);
    -ms-transform: translateX(72px);
    -o-transform: translateX(72px);
  }

  100% {
    transform: translateX(-1px);
    -webkit-transform: translateX(-1px);
    -moz-transform: translateX(-1px);
    -ms-transform: translateX(-1px);
    -o-transform: translateX(-1px);
  }
}

@-webkit-keyframes divider-move {
  0% {
    transform: translateX(-1px);
    -webkit-transform: translateX(-1px);
    -moz-transform: translateX(-1px);
    -ms-transform: translateX(-1px);
