  width: 28px;
  /* Adjust the width of the toggle circle */
  line-height: 20px;
  border-radius: 50%;
  background: #fff;
  display: block;
  position: absolute;
  left: 22px;
  top: 0px;
  /* Adjust the top position of the toggle circle */
  transition: all 0.3s ease-in-out;
}

